import React, { Component } from 'react'
import { BrowserRouter as Router, Link, Switch, Route } from 'react-router-dom'
import './App.css';

import Cats from './pages/Cats'
import NewCat from './pages/NewCat'
import Header from './components/Header'
import Navigation from './components/Navigation'

export default class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            cats: [
                      {
                        id: 1,
                        name: 'Morris',
                        age: 2,
                        enjoys: "Long walks on the beach."
                      },
                      {
                        id: 2,
                        name: 'Paws',
                        age: 4,
                        enjoys: "Snuggling by the fire."
                      },
                      {
                        id: 3,
                        name: 'Mr. Meowsalot',
                        age: 12,
                        enjoys: "Being in charge."
                      }
                ]
        }
    }

    handleNewCat = (newCatForm) => {
        let newArr = this.state.cats
        console.log(newArr);
        let newCat = newCatForm
        newCatForm.id = newArr.length + 1
        newCatForm.age = parseInt(newCatForm.age)
        newArr.push(newCat)
        this.setState({ cats: newArr })
    }

    render() {
        return (
            <div>
                <Navigation />
                <Header />
                    <Router>
                        <Switch>
                            <Route
                            exact path="/cats"
                            render={(props) =>
                            <Cats cats={ this.state.cats}/>
                            }
                            />
                            <Route
                            exact path="/newcat"
                            render={(props) =>
                            <NewCat {...props} handleNewCat={this.handleNewCat}/>
                            }
                            />
                        </Switch>
                    </Router>
            </div>
        )
    }
}
